/* istanbul ignore file */

let error

const _getClient = name => {
  try {
    return Object.assign({ name }, require(name))
  } catch (err) {
    if (name === '@sap/hana-client') {
      error = err
      return _getClient('hdb')
    }

    throw error
  }
}

module.exports = _getClient('@sap/hana-client')
